import React, { Component } from 'react';
import { Button, CardPanel, Col, Row, Icon } from 'react-materialize';
import M from 'materialize-css';
import { getDocumentNamesForUser, downloadDocument, uploadDocument} from "../Api";



class DocumentManager extends Component {
    constructor(props) {
      super(props);
      this.state = {
        documents: [],
        selectedFile: null,
      };
    }

    async componentDidMount() {
      M.AutoInit(); // Initialize Materialize components (optional, depending on usage)
      const [success, data] = await getDocumentNamesForUser();
      if (success) {
        this.setState({ documents: data });
      }
    }

    handleFileChange = (event) => {
      this.setState({ selectedFile: event.target.files[0] });
    };

    handleUpload = async () => {
      const { selectedFile } = this.state;
      if (selectedFile) {
        const [success, response] = await uploadDocument(selectedFile);
        if (success) {
          M.toast({ html: 'File uploaded successfully!' });
          const [success, data] = await getDocumentNamesForUser();
          if (success) {
            this.setState({ documents: data });
          }
        } else {
          M.toast({ html: 'Failed to upload the file' });
        }
      } else {
        M.toast({ html: 'Please select a file to upload.' });
      }
    };

    render() {
      const { documents } = this.state;

      return (
        <div className="container">
          {/* Check if there are any documents */}
          {documents.length > 0 ? (
            <Row>
              <h4>Documents</h4>
              {documents.map((doc, index) => (
                <Col key={index} s={12} m={6} l={4}>
                  <CardPanel className="card-panel hoverable">
                    <h5>{doc.OrignalFileName}</h5>
                    <Button
                      onClick={() => downloadDocument(doc.SystemFileName)}
                      className="blue"
                    >
                      <Icon left>download</Icon>
                      Download
                    </Button>
                  </CardPanel>
                </Col>
              ))}
            </Row>
          ) : (
            <Row>
              <Col s={12}>
                <CardPanel className="card-panel hoverable">
                  <h5>There are no documents.</h5>
                </CardPanel>
              </Col>
            </Row>
          )}

          {/* File upload section */}
          <Row>
            <Col s={12}>
              <h4>Upload Document</h4>
              <input type="file" onChange={this.handleFileChange} />
              <Button onClick={this.handleUpload} className="green">
                <Icon left>upload</Icon>
                Upload
              </Button>
            </Col>
          </Row>
        </div>
      );
    }
  }

  export default DocumentManager;
